import React from "react";
// TODO: import useFormik from formik library
import {useFormik} from 'formik'

function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      console.log('form:',values);
    },
    //If the username or password inputs are empty, display the message "Field required" under the text input.
    validate: values =>{
      let errors = {};
      //if the username is not an email format, display the message "Username should be an email" under the text input.
      if(!values.email) errors.email = 'Field Required';
      if(!values.password) errors.password = 'Field Required';
      //if the username and password pass the above validations, then display the message "Login Successful" in an alert box.
      return errors;

    }
  });
  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Email:</div>
        <input type="text" name="emailField" onChange={formik.handleChange} value={formik.values.emailField}/>
        {formik.errors.emailField ? <div style={{color:'red'}}>{formik.errors.emailField}</div> : null}
        <div>Password:</div>
        <input type="text" name="pswField" onChange={formik.handleChange} value={formik.values.password}/><br/>
        {formik.errors.password ? <div style={{color:'red'}}>{formik.errors.password}</div> : null}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
